const Octokit = require('@octokit/rest');
const pullreq = require('../pullreq');

(async function() {
  const octokit = Octokit({
    auth: process.env.GITHUB_TOKEN,
  })
  const pull = await pullreq.prepare({
    octokit,
    owner: 'grassedge',
    repo: 'test-action',
    base: 'bbb',
    head: 'aaa',
  });
  console.log(pull);

})().catch(e => console.log(e));
